<?phpnamespace Kna\HalBundle\Controller;use FOS\RestBundle\Controller\FOSRestController;use Kna\HalBundle\Filter\FilterFactoryInterface;use Kna\HalBundle\Filter\FilterInterface;use Kna\HalBundle\Representation\RepresentationFactoryInterface;abstract class BaseRestController extends FOSRestController{    public function getFilterFactory(): FilterFactoryInterface    {        return $this->get('kna_hal.filter_factory');    }    public function createFilter(string $type, array $options = []): FilterInterface    {        return $this->getFilterFactory()->create($type, $options);    }    public function getRepresentationFactory(): RepresentationFactoryInterface    {        return $this->get('kna_hal.representation_factory');    }    public function createRepresentation(string $name, ...$args)    {        return call_user_func_array(            [$this->getRepresentationFactory(), 'create'],            array_merge([$name], $args)        );    }}