<?phpnamespace Kna\HalBundle\DependencyInjection;use Kna\HalBundle\Representation\RepresentationProviderInterface;use Symfony\Component\Config\FileLocator;use Symfony\Component\DependencyInjection\ContainerBuilder;use Symfony\Component\DependencyInjection\Extension\Extension;use Symfony\Component\DependencyInjection\Loader\XmlFileLoader;class KnaHalExtension extends Extension{    /**     * {@inheritdoc}     */    public function load(array $configs, ContainerBuilder $container): void    {        $container            ->registerForAutoconfiguration(RepresentationProviderInterface::class)            ->addTag('kna_hal.representation_provider')        ;        $loader = new XmlFileLoader($container, new FileLocator(__DIR__.'/../Resources/config'));        $loader->load('filter.xml');        $loader->load('representation.xml');    }}