<?phpnamespace Kna\HalBundle\Manager;use Doctrine\ODM\MongoDB\DocumentManager;use Doctrine\ODM\MongoDB\Query\Builder;use Kna\HalBundle\Filter\FilterInterface;use Pagerfanta\Adapter\DoctrineODMMongoDBAdapter;use Pagerfanta\Pagerfanta;abstract class BaseDocumentManager extends BaseManager implements PageableManagerInterface{    /**     * @return DocumentManager     */    public function getDocumentManager(): DocumentManager    {        return $this->getObjectManager();    }    /**     * {@inheritdoc}     */    public function getPager(FilterInterface $filter): Pagerfanta    {        $queryBuilder = $this->resolveQueryBuilder($filter);        $adapter = new DoctrineODMMongoDBAdapter($queryBuilder);        $pager = new Pagerfanta($adapter);        $pager->setMaxPerPage($filter->get('limit'));        $pager->setCurrentPage($filter->get('page'));        return $pager;    }    /**     * @param FilterInterface $filter     * @return Builder     */    public function resolveQueryBuilder(FilterInterface $filter): Builder {        throw new \RuntimeException('Method not implemented');    }}