<?phpnamespace Kna\HalBundle\Manager;use Doctrine\Common\Persistence\ObjectManager;use Doctrine\Common\Persistence\ObjectRepository;interface ManagerInterface{    /**     * @return array     */    public function findAll(): array;    /**     * @param integer $id     * @return object|null     */    public function find($id);    /**     * @param array $criteria     * @param array|null $orderBy     * @param null $limit     * @param null $offset     * @return array     */    public function findBy(array $criteria, array $orderBy = null, $limit = null, $offset = null): array;    /**     * @param array $criteria     * @return null|object     */    public function findOneBy(array $criteria);    /**     * @return mixed     */    public function create();    /**     * @param object $object     * @param bool|true $andFlush     */    public function save($object, $andFlush = true): void;    /**     * @param object $object     * @param bool|true $andFlush     */    public function delete($object, $andFlush = true): void;    /**     * @return ObjectManager     */    public function getObjectManager(): ObjectManager;    /**     * @return ObjectRepository     */    public function getRepository(): ObjectRepository;    /**     * @return string     */    public function getClass(): string;}